import React ,{Component} from 'react';
import 'bootstrap/dist/css/bootstrap.css';
class Header extends Component{
	 constructor(){
   super();
   this.OpenNav = this.OpenNav.bind(this);
	   this.CloseNav = this.CloseNav.bind(this);
 }
 
 state = {
     
		logo: "logo.jpg"
    };
  render(){
	    var x={
			backgroundColor: 'dodgerblue',
			height:"20%",
			position: 'relative'
			
    }
    return(
	<div>
	 <span class="text_transformation" onClick={this.OpenNav}>&#9776; jobglode</span>
	 
	 
	 
						<div style={ x } id="mySidenav" class="sidenav" >
                                  <a href="javascript:void(0)" class="closebtn" onClick={this.CloseNav}>&times;</a>
                                  <div class="container" >
                                          <div class="row">
                                                    <div class="col-md-3" ></div>
                                                    <div class="col-md-3" >
                                                          <img src={this.state.logo}/>
                                                    </div>
                                                    <div class="vertical" ></div>
                                                    <div class="col-md-3" >
                                                          <a class="text_transformation"href="html/top_rated_movies.html">helpdesk</a>
                                                          <a class="text_transformation"href="html/upcoming_movies.html">tutorial</a>
														  <a class="text_transformation"href="html/upcoming_movies.html">feedback</a>
                                                    </div>
                                                    <div class="col-md-3" ></div>
                                          </div>
                                  </div>
                          </div>
    </div>
	  
	  
    );
}




OpenNav(){
	
   var element = document.getElementById("mySidenav");

      element.style.display = " ";
	
}
CloseNav(){
	 var element = document.getElementById("mySidenav");

	 element.style.display = "none";
	
}
}
export default Header;
