import React ,{Component} from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import FooterFormat from  './footer.js';
import HeaderFormat from  './header.js';
import BlockNav from  './blockNav.js';
import BlockFilter from  './blockfilter.js';
import Modal from "react-animated-modal";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faBriefcase,faAddressCard,faCalendar,faUser,faMoneyBillAlt } from '@fortawesome/free-solid-svg-icons'
import company from './images/company.JPG';
class AddCandidate extends Component{
		 constructor(){
   super();

	this.candidateMain = this.candidateMain.bind(this);
	this.val = this.val.bind(this);
 }
    
  state = {
	  showcandidateModal: false,
	  
	  details:[],
	timingList: [
        {
            id: 1,
            title: 'front end developer',
            company_name:'company A',
			dept: 'web development',
			company_location:'chandigarh',
			number:'123456',
			exp:"7-10 yrs",
			job_type:"full time",
			candidate:'3',
			stripend:"23-25 lakhs"
        },
        {
            id: 2,
            title:'back  end developer',
             company_name:'company B',
			dept: 'web development',
			company_location:'noida',
				number:'345678',
				exp:"7-10 yrs",job_type:"full time",
				candidate:'3',
			stripend:"23-25 lakhs"
        },
        {
            id: 3,
                      title:'PHP developer',
            company_name:'company C',
			dept: 'web development',
			company_location:'bangaluru',
				number:'678901',
				exp:"7-10 yrs",
				job_type:"full time",
				candidate:'3',
			stripend:"23-25 lakhs"
        },
		{
            id: 4,
                        title:'laravel developer',
             company_name:'company D',
			dept: 'web development',
			company_location:'pune',
				number:'456321',
				exp:"7-10 yrs",
				job_type:"full time",
				candidate:'3',
			stripend:"23-25 lakhs"
        },
        {
            id: 5,
                        title:'React end developer',
             company_name:'company E',
			dept: 'web development',
			company_location:'mumbai',
				number:'765432',
				exp:"7-10 yrs",
				job_type:"full time",
				candidate:'3',
			stripend:"23-25 lakhs"
        },
        {
            id: 6,
                        title:'MERN stack developer',
             company_name:'company F',
			dept: 'web development',
			company_location:'hyderabad',
				number:'987654',
				exp:"7-10 yrs",
				job_type:"full time",
				candidate:'3',
			stripend:"23-25 lakhs"
        }
		
	]
};


  render() {
	  var container_image={
		borderRadius: '2% 40%',
		border:'2px solid black'
	}
      return (
          <div className="main-container">
		  		<HeaderFormat/><br/>
				<Modal
                    visible={this.state.showcandidateModal}
                    closemodal={() => this.setState({ showcandidateModal: false })}
                    type="flipInX"
                >
				id number :-{this.state.details.id}<br/>
				job profile:-{this.state.details.title}({this.state.details.job_type})<br/>
				company:-{this.state.details.company_name},{this.state.details.company_location}<br/>
				<form id='main' action="#" onSubmit={this.val} >

<input id='main-inpt' type="number" ref="name" placeholder="add candidates"required />


<input id='main-btn' type="submit" value="submit"/>
</form>
				
                </Modal>
				
				
				
				<div className="container-fluid">
				<div className="row">
				<div className="col-md-2"  style={{border:'2px solid black',backgroundColor:'black'}} ><BlockNav /></div>
				<div className="col-md-9" style={{border:'2px solid red'}}>
              <span className="timing-list-container" style={{position:'relative',marginTop:'30%'}}>
                  {
                      this.state.timingList.map((timing,i) => {
                          return <span style={{border:'2px solid black'}}key={timing.id}>
						  <span className="row"><img src={company} style={container_image}/></span>
						   <span className="row">{timing.title}</span>
                              <span className="row">{timing.company_name} , {timing.dept}</span>
						   <span className="row">{timing.company_location}</span>
                           <hr style={{border:'2px groove black',width:'60%'}}/>   
						    <span className="row"><div className="col-md-6"> <FontAwesomeIcon icon={faAddressCard} style={{fontSize:'200%'}}/> {timing.number}</div><div className="col-md-6"> <FontAwesomeIcon icon={faBriefcase} style={{fontSize:'200%'}}/> {timing.exp}</div></span>
								   <span className="row"><FontAwesomeIcon icon={faCalendar} style={{fontSize:'200%'}}/>{timing.job_type}</span>
								    <hr style={{border:'2px groove black',width:'60%'}}/>  
									 <span className="row"><div className="col-md-6"> <FontAwesomeIcon icon={faUser} style={{fontSize:'200%'}}/> {timing.candidate} candidates</div><div className="col-md-6"> <FontAwesomeIcon icon={faMoneyBillAlt} style={{fontSize:'200%'}}/> {timing.stripend}</div></span>
							  <button  onClick={e => this.candidateMain(timing, i)}>ADD CANDIDATE</button>
							   
								
                          </span>
                      })
                  }
              </span>
			  </div>
			  <div className="col-md-1" style={{border:'2px solid black',backgroundColor:'black'}} ><BlockFilter /></div>
			  </div>
			  </div>
			  
			  
			  
			  
			  <FooterFormat/>
          </div>
      );
  }
  candidateMain(data, i) {
  
    this.setState({details :data});
   console.log(this.state.details);
   this.setState({ showcandidateModal: true })
  }
 
  val(){
    var candidated_to_add = this.refs.name.value;
	var array_index = this.state.details.id;
	alert(candidated_to_add + " will be added to job portal of id "+ array_index);
	

  }
 }
export default AddCandidate
