import React ,{Component} from 'react';
class Header extends Component{
	 constructor(){
   super();
   this.OpenNav = this.OpenNav.bind(this);
	   this.CloseNav = this.CloseNav.bind(this);
 }
 
 state = {
        ShowNav: false,
		logo: "logo.jpg"
    };
  render(){

    return(
	<div>
	 <span class="text_transformation" style="font-size:30px;cursor:pointer" onClick={this.OpenNav()}>&#9776; jobglode</span>
      //<div style ={{position:'relative',left:'40%',backgroundColor:'dodgerblue',width:"20%",fontSize :'200%',textAligin:'centre'}}>ToDo List App</div>
	  
	   <div visible={this.state.ShowNav} id="mySidenav" class="sidenav" >
                                  <a href="javascript:void(0)" class="closebtn"  onClick={this.CloseNav()}>&times;</a>
                                  <div class="container" style="position:absolute;top:30%;">
                                          <div class="row">
                                                    <div class="col-md-3" ></div>
                                                    <div class="col-md-3" >
                                                          <img src={this.state.logo} style="border-radius: 2% 40%;"/>
                                                    </div>
                                                    <div class="vertical" ></div>
                                                    <div class="col-md-3" >
                                                          <a class="text_transformation"href="html/top_rated_movies.html">helpdesk</a>
                                                          <a class="text_transformation"href="html/upcoming_movies.html">tutorial</a>
														  <a class="text_transformation"href="html/upcoming_movies.html">feedback</a>
                                                    </div>
                                                    <div class="col-md-3" ></div>
                                          </div>
                                  </div>
                          </div>
						  
						  </div>

    );
}




OpenNav(){
	this.setState({ showNav: true })
}
CloseNav(){
	this.setState({ showNav: false })
}
}
export default Header;
