import React ,{Component} from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import FooterFormat from  './footer.js';
import HeaderFormat from  './header.js';
import Modal from "react-animated-modal";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCoffee } from '@fortawesome/free-solid-svg-icons'
import company from './images/company.JPG';
class RecruiterPortal extends Component{
		 constructor(){
   super();

	this.acceptedMain = this.acceptedMain.bind(this);
	this.rejectedMain = this.rejectedMain.bind(this);
 }
 
	    
  state = {
	  showacceptedModal: false,
	  showrejectedtedModal: false ,
	  details:[],
	timingList: [
        {
            id: 1,
            title: 'front end developer',
            company_name:'company A',
			dept: 'web development',
			company_location:'chandigarh'
        },
        {
            id: 2,
            title:'back  end developer',
             company_name:'company B',
			dept: 'web development',
			company_location:'noida'
        },
        {
            id: 3,
                      title:'PHP developer',
            company_name:'company C',
			dept: 'web development',
			company_location:'bangaluru'
        },
		{
            id: 4,
                        title:'laravel developer',
             company_name:'company D',
			dept: 'web development',
			company_location:'pune'
        },
        {
            id: 5,
                        title:'React end developer',
             company_name:'company E',
			dept: 'web development',
			company_location:'mumbai'
        },
        {
            id: 6,
                        title:'MERN stack developer',
             company_name:'company F',
			dept: 'web development',
			company_location:'hyderabad'
        }
		
	]
};


  render() {
	  var container_image={
		borderRadius: '2% 40%',
		border:'2px solid black'
	}
      return (
          <div className="main-container">
		  		<HeaderFormat/>
				<Modal
                    visible={this.state.showacceptedModal}
                    closemodal={() => this.setState({ showacceptedModal: false })}
                    type="flipInX"
                >
				{this.state.details.id}
				is accepted
                </Modal>
				
				<Modal
                    visible={this.state.showrejectedModal}
                    closemodal={() => this.setState({ showrejectedModal: false })}
                    type="flipInX"
                >
				{this.state.details.id}
				 is rejected
                </Modal>
				
				<div className="container">
				<div className="row">
				<div className="col-md-1"></div>
				<div className="col-md-10" style={{border:'2px solid red'}}>
              <span className="timing-list-container">
                  {
                      this.state.timingList.map((timing,i) => {
                          return <span style={{border:'2px solid black'}}key={timing.id}>
						  <span className="row"><img src={company} style={container_image}/></span>
						   <span className="row">{timing.title}</span>
                              <span className="row">{timimg.company_name}</span>
						   <span className="row">{timing.company_location}</span>
                              
							  <button  onClick={e => this.acceptedMain(timing, i)}>ACCEPT</button>
							    <button  onClick={e => this.rejectedMain(timing, i)}>REJECT</button>
								
                          </span>
                      })
                  }
              </span>
			  </div>
			  <div className="col-md-1"></div>
			  </div>
			  </div>
			  
			  
			  
			  
			  <FooterFormat/>
          </div>
      );
  }
  acceptedMain(data, i) {
  
    this.setState({details :data});
   console.log(this.state.details);
   this.setState({ showacceptedModal: true })
  }
  rejectedMain(data, i) {
  
    this.setState({details :data});
   console.log(this.state.details);
   this.setState({ showrejectedModal: true })
  }
 }
export default RecruiterPortal
