import React ,{Component} from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import FooterFormat from  './footer.js';
import HeaderFormat from  './header.js';
import Modal from "react-animated-modal";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCoffee } from '@fortawesome/free-solid-svg-icons'
class RecruiterPortal extends Component{
		 constructor(){
   super();

	this.main = this.main.bind(this);
 }
 
	    
  state = {
	  showModal: false,
	  details:[],
	timingList: [
        {
            id: 1,
            time: '9 AMBWRHJGMJLIL;OdFGGR',
            isMeetingSet: false
        },
        {
            id: 2,
            time: '10 AMNBJNGVUFBFCyUHG',
            isMeetingSet: false
        },
        {
            id: 3,
            time: '11 AMfvbjhvnjmwKfmrBGJRW',
            isMeetingSet: false
        },
		{
            id: 4,
            time: '9 AMBWRHJGMJLIL;OdFGGR',
            isMeetingSet: false
        },
        {
            id: 5,
            time: '10 AMNBJNGVUFBFCyUHG',
            isMeetingSet: false
        },
        {
            id: 6,
            time: '11 AMfvbjhvnjmwKfmrBGJRW',
            isMeetingSet: false
        },
		{
            id: 7,
            time: '9 AMBWRHJGMJLIL;OdFGGR',
            isMeetingSet: false
        },
        {
            id: 8,
            time: '10 AMNBJNGVUFBFCyUHG',
            isMeetingSet: false
        },
        {
            id: 9,
            time: '11 AMfvbjhvnjmwKfmrBGJRW',
            isMeetingSet: false
        }
	]
};


  render() {
      return (
          <div className="main-container">
		  		<HeaderFormat/>
				<Modal
                    visible={this.state.showacceptedModal}
                    closemodal={() => this.setState({ showModal: false })}
                    type="flipInX"
                >
				{this.state.details.id}
                </Modal>
				
				<Modal
                    visible={this.state.showrejectedtedModal}
                    closemodal={() => this.setState({ showModal: false })}
                    type="flipInX"
                >
				{this.state.details.id}
                </Modal>
				
				<div className="container">
				<div className="row">
				<div className="col-md-2"></div>
				<div className="col-md-8" style={{border:'2px solid red'}}>
              <span className="timing-list-container">
                  {
                      this.state.timingList.map((timing,i) => {
                          return <span  style={{border:'2px solid black'}}key={timing.id}>
                              <span onClick={e => this.main(timing, i)}>{timing.time}</span>
                          </span>
                      })
                  }
              </span>
			  </div>
			  <div className="col-md-2"></div>
			  </div>
			  </div>
			  
			  
			  
			  
			  <FooterFormat/>
          </div>
      );
  }
  main(data, i) {
  
    this.setState({details :data});
   console.log(this.state.details);
   this.setState({ showModal: true })
  }

 }
export default RecruiterPortal
