import React ,{Component} from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import Icon from '@material-ui/core/Icon';

import  './header.css';
class Header extends Component{
	 constructor(){
   super();
   this.shootopen = this.shootopen.bind(this);
	   this.shootclose = this.shootclose.bind(this);
 }
 
 state = {
	 width:true,
     showStore:false,
		logo: "logo.jpg"
    };
  render(){
	    var x={
			display :"none"
			
    }
		    var container_style={
			position:'absolute',
			top:'30%',
			
			
    }
	var container_image={
		borderRadius: '2% 40%'
	}
    return(
	<div>
	 <div><span className="text_transformation" onClick={this.shootopen}>&#9776;</span> jobglobe 
	  <div className="dropdown">
    <span className="btn btn-default dropdown-toggle"  data-toggle="dropdown">Tutorials
    <span className="caret"></span></span>
    <ul className="dropdown-menu">
      <li className="dropdown-header">Dropdown header 1</li>
      <li><a href="#">HTML</a></li>
      <li><a href="#">CSS</a></li>
      <li><a href="#">JavaScript</a></li>
      <li className="divider"></li>
      <li className="dropdown-header">Dropdown header 2</li>
      <li><a href="#">About Us</a></li>
    </ul>
  </div>
	 </div>
	 <div id="mySidenav" className="sidenav" style={{display: this.state.showStore ? 'block' : 'none', width:this.state.width?'0%':'100%' }}>
                                  <a  className="closebtn" onClick={this.shootclose} >&times;</a>
                                  <div className="container" style={container_style}>
                                          <div className="row">
                                                    <div className="col-md-3" ></div>
                                                    <div className="col-md-3" >
                                                          <img src={this.state.logo} style={container_image}/>
                                                    </div>
                                                    <div className="vertical" ></div>
                                                    <div className="col-md-3" >
                                                          <a className="text_transformation"href="html/top_rated_movies.html">a</a>
                                                          <a className="text_transformation"href="html/upcoming_movies.html">b</a>
                                                    </div>
                                                    <div className="col-md-3" ></div>
                                          </div>
                                  </div>
                          </div>
	 
	 
						
    </div>
	  
	  
    );
}




shootopen(){
this.setState({ showStore: true,width:false })
}
shootclose(){
	this.setState({ showStore: false ,width:true})
	
}

}
export default Header;
