import React ,{Component} from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import FooterFormat from  './footer.js';
import HeaderFormat from  './header.js';
import Modal from "react-animated-modal";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faBriefcase,faAddressCard,faCalender,faUser,faMoneyBillAlt } from '@fortawesome/free-solid-svg-icons'
import company from './images/company.JPG';
class RecruiterPortal extends Component{
		 constructor(){
   super();

	this.acceptedMain = this.acceptedMain.bind(this);
	this.rejectedMain = this.rejectedMain.bind(this);
 }
 
	    
  state = {
	  showacceptedModal: false,
	  showrejectedtedModal: false ,
	  details:[],
	timingList: [
        {
            id: 1,
            title: 'front end developer',
            company_name:'company A',
			dept: 'web development',
			company_location:'chandigarh',
			number:'123456',
			exp:"7-10 yrs",
			job_type:"full time",
			candidate:'3 candidates',
			stripend:"23-25 lakhs"
        },
        {
            id: 2,
            title:'back  end developer',
             company_name:'company B',
			dept: 'web development',
			company_location:'noida',
				number:'345678',
				exp:"7-10 yrs",job_type:"full time",
				candidate:'3 candidates',
			stripend:"23-25 lakhs"
        },
        {
            id: 3,
                      title:'PHP developer',
            company_name:'company C',
			dept: 'web development',
			company_location:'bangaluru',
				number:'678901',
				exp:"7-10 yrs",
				job_type:"full time",
				candidate:'3 candidates',
			stripend:"23-25 lakhs"
        },
		{
            id: 4,
                        title:'laravel developer',
             company_name:'company D',
			dept: 'web development',
			company_location:'pune',
				number:'456321',
				exp:"7-10 yrs",
				job_type:"full time",
				candidate:'3 candidates',
			stripend:"23-25 lakhs"
        },
        {
            id: 5,
                        title:'React end developer',
             company_name:'company E',
			dept: 'web development',
			company_location:'mumbai',
				number:'765432',
				exp:"7-10 yrs",
				job_type:"full time",
				candidate:'3 candidates',
			stripend:"23-25 lakhs"
        },
        {
            id: 6,
                        title:'MERN stack developer',
             company_name:'company F',
			dept: 'web development',
			company_location:'hyderabad',
				number:'987654',
				exp:"7-10 yrs",
				job_type:"full time",
				candidate:'3 candidates',
			stripend:"23-25 lakhs"
        }
		
	]
};


  render() {
	  var container_image={
		borderRadius: '2% 40%',
		border:'2px solid black'
	}
      return (
          <div className="main-container">
		  		<HeaderFormat/>
				<Modal
                    visible={this.state.showacceptedModal}
                    closemodal={() => this.setState({ showacceptedModal: false })}
                    type="flipInX"
                >
				{this.state.details.id}
				is accepted
                </Modal>
				
				<Modal
                    visible={this.state.showrejectedModal}
                    closemodal={() => this.setState({ showrejectedModal: false })}
                    type="flipInX"
                >
				{this.state.details.id}
				 is rejected
                </Modal>
				
				<div className="container">
				<div className="row">
				<div className="col-md-1"></div>
				<div className="col-md-10" style={{border:'2px solid red'}}>
              <span className="timing-list-container">
                  {
                      this.state.timingList.map((timing,i) => {
                          return <span style={{border:'2px solid black'}}key={timing.id}className="col-md-3">
						  <span className="row"><img src={company} style={container_image}/></span>
						   <span className="row">{timing.title}</span>
                              <span className="row">{timing.company_name} , {timing.dept}</span>
						   <span className="row">{timing.company_location}</span>
                           <hr style={{border:'2px groove black',width:'60%'}}/>   
						    <span className="row"><div className="col-md-6"> <FontAwesomeIcon icon={faAddressCard} style={{fontSize:'200%'}}/> {timing.number}</div><div className="col-md-6"> <FontAwesomeIcon icon={faBriefcase} style={{fontSize:'200%'}}/> {timing.exp}</div></span>
								   <span className="row"><FontAwesomeIcon icon={faBriefcase} style={{fontSize:'200%'}}/>{timing.job_type}</span>
								    <hr style={{border:'2px groove black',width:'60%'}}/>  
									 <span className="row"><div className="col-md-6"> <FontAwesomeIcon icon={faUser} style={{fontSize:'200%'}}/> {timing.candidate}</div><div className="col-md-6"> <FontAwesomeIcon icon={faMoneyBillAlt} style={{fontSize:'200%'}}/> {timing.stripend}</div></span>
							  <button  onClick={e => this.acceptedMain(timing, i)}>ACCEPT</button>
							    <button  onClick={e => this.rejectedMain(timing, i)}>REJECT</button>
								
                          </span>
                      })
                  }
              </span>
			  </div>
			  <div className="col-md-1"></div>
			  </div>
			  </div>
			  
			  
			  
			  
			  <FooterFormat/>
          </div>
      );
  }
  acceptedMain(data, i) {
  
    this.setState({details :data});
   console.log(this.state.details);
   this.setState({ showacceptedModal: true })
  }
  rejectedMain(data, i) {
  
    this.setState({details :data});
   console.log(this.state.details);
   this.setState({ showrejectedModal: true })
  }
 }
export default RecruiterPortal
