import React ,{Component} from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import FooterFormat from  './footer.js';
import HeaderFormat from  './header.js';
import Modal from "react-animated-modal";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCoffee } from '@fortawesome/free-solid-svg-icons'
class RecruiterPortal extends Component{
  state = {
	timingList: [
        {
            id: 1,
            time: '9 AM',
            isMeetingSet: false
        },
        {
            id: 2,
            time: '10 AM',
            isMeetingSet: false
        },
        {
            id: 3,
            time: '11 AM',
            isMeetingSet: false
        }
	]
};


  render() {
      return (
          <div className="main-container">
		  		<HeaderFormat/>
				
				
				
				
				<div className="container">
				<div className="row">
				<div className="col-md-2"></div>
				<div className="col-md-8" style={{border:'2px solid red'}}>
              <span className="timing-list-container">
                  {
                      this.state.timingList.map(timing => {
                          return <span className="col-md-12" style={{border:'2px solid black'}}key={timing.id}>
                              <span>{timing.time}</span>
                          </span>
                      })
                  }
              </span>
			  </div>
			  <div className="col-md-2"></div>
			  </div>
			  </div>
			  
			  
			  
			  
			  <FooterFormat/>
          </div>
      );
  }

 }
export default RecruiterPortal
