import React ,{Component} from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faGlobeAfrica } from '@fortawesome/free-solid-svg-icons'
import { BrowserRouter as Router, Route, Link, Switch } from 'react-router-dom'; 
import Jobs from './RecruiterPortal.js'; 
import Accepted from './accepted.js'; 
import Rejected from './rejected.js'; 
import Candidate from './addcandidate.js';
class BlockNav extends Component{

  render(){
	  
	
    return(
	<div>

		
	<Router> 
    <div className="container">
   
			 <div className="row" style={{position:'fixed',zIndex:'99',backgroundColor:'black',color:'white',width:'15%'}}>
			 navigation
					 <br/><br/>
					 
					  <Link to="/"><div className="col-md-12"><FontAwesomeIcon icon={faGlobeAfrica} style={{fontSize:'200%'}}/>dashboard</div></Link>  
					   <hr className="col-md-2" style={{border:'2px groove black',width:'20%',marginLeft:'0'}}/>
					  <Link to="/addcandidates"><div className="col-md-12"><FontAwesomeIcon icon={faGlobeAfrica} style={{fontSize:'200%'}}/>candidates</div></Link>
						 <hr className="col-md-2" style={{border:'2px groove black',width:'20%',marginLeft:'0'}}/>
					  <Link to="/livejobs"><div className="col-md-12"><FontAwesomeIcon icon={faGlobeAfrica} style={{fontSize:'200%'}}/>jobs</div> </Link>
					   <hr className="col-md-2" style={{border:'2px groove black',width:'20%',marginLeft:'0'}}/>
					  <Link to="/rejectedjobs"><div className="col-md-12"><FontAwesomeIcon icon={faGlobeAfrica} style={{fontSize:'200%'}}/>rejected</div></Link>
					   <hr className="col-md-2" style={{border:'2px groove black',width:'20%',marginLeft:'0'}}/>
					  <Link to="/acceptedjobs"><div className="col-md-12" > <FontAwesomeIcon icon={faGlobeAfrica} style={{fontSize:'200%'}}/>accepted</div></Link>
				 </div>
				 <Switch> 
			<Route exact path='/addcandidates' component={Candidate}></Route> 
			<Route exact path='/livejobs' component={Jobs}></Route> 
			<Route exact path='/rejectedjobs' component={Rejected}></Route> 
			<Route exact path='/acceptedjobs' component={Contact}></Route> 
			</Switch>
				  
		</div>
			</Router> 
	</div>
    );
}

}
export default BlockNav;
