import React ,{Component} from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import FooterFormat from  './footer.js';
import HeaderFormat from  './header.js';
import App from  './app.js';
import BlockFilter from  './blockfilter.js';
import Modal from "react-animated-modal";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faBriefcase,faAddressCard,faCalendar,faUser,faMoneyBillAlt } from '@fortawesome/free-solid-svg-icons'
import company from './images/company.JPG';
class Accepted extends Component{

	    
  state = {
	  
	timingList: [
        {
            id: 1,
            title: 'front end developer',
            company_name:'company A',
			dept: 'web development',
			company_location:'chandigarh',
			number:'123456',
			exp:"7-10 yrs",
			job_type:"full time",
			candidate:'3 candidates',
			stripend:"23-25 lakhs"
        },
        {
            id: 2,
            title:'back  end developer',
             company_name:'company B',
			dept: 'web development',
			company_location:'noida',
				number:'345678',
				exp:"7-10 yrs",job_type:"full time",
				candidate:'3 candidates',
			stripend:"23-25 lakhs"
        },
        {
            id: 3,
                      title:'PHP developer',
            company_name:'company C',
			dept: 'web development',
			company_location:'bangaluru',
				number:'678901',
				exp:"7-10 yrs",
				job_type:"full time",
				candidate:'3 candidates',
			stripend:"23-25 lakhs"
        },
		{
            id: 4,
                        title:'laravel developer',
             company_name:'company D',
			dept: 'web development',
			company_location:'pune',
				number:'456321',
				exp:"7-10 yrs",
				job_type:"full time",
				candidate:'3 candidates',
			stripend:"23-25 lakhs"
        }
        
		
	]
};


  render() {
	  var container_image={
		borderRadius: '2% 40%',
		border:'2px solid black'
	}
      return (
          <div className="main-container">
		  		<HeaderFormat/><br/>
				
				<div className="container-fluid">
				<div className="row">
				<div className="col-md-2"  style={{border:'2px solid black',backgroundColor:'black'}} ><App /></div>
				<div className="col-md-9" style={{border:'2px solid red'}}>
              <span className="timing-list-container" style={{position:'relative',marginTop:'30%'}}>
                  {
                      this.state.timingList.map((timing,i) => {
                          return <span style={{border:'2px solid black'}}key={timing.id}>
						  <span className="row"><img src={company} style={container_image}/></span>
						   <span className="row">{timing.title}</span>
                              <span className="row">{timing.company_name} , {timing.dept}</span>
						   <span className="row">{timing.company_location}</span>
                           <hr style={{border:'2px groove black',width:'60%'}}/>   
						    <span className="row"><div className="col-md-6"> <FontAwesomeIcon icon={faAddressCard} style={{fontSize:'200%'}}/> {timing.number}</div><div className="col-md-6"> <FontAwesomeIcon icon={faBriefcase} style={{fontSize:'200%'}}/> {timing.exp}</div></span>
								   <span className="row"><FontAwesomeIcon icon={faCalendar} style={{fontSize:'200%'}}/>{timing.job_type}</span>
								    <hr style={{border:'2px groove black',width:'60%'}}/>  
									 <span className="row"><div className="col-md-6"> <FontAwesomeIcon icon={faUser} style={{fontSize:'200%'}}/> {timing.candidate}</div><div className="col-md-6"> <FontAwesomeIcon icon={faMoneyBillAlt} style={{fontSize:'200%'}}/> {timing.stripend}</div></span>
							  <button type="button" class="btn btn-lg btn-success" disabled>accepted</button>
							
							    
								
                          </span>
                      })
                  }
              </span>
			  </div>
			  <div className="col-md-1" style={{border:'2px solid black',backgroundColor:'black'}} ><BlockFilter /></div>
			  </div>
			  </div>
			  
			  
			  
			  
			  <FooterFormat/>
          </div>
      );
  }

 }
export default Accepted
